import { FormControl, FormLabel, Input, InputGroup, InputRightElement, VStack, Button } from '@chakra-ui/react';
import React, { useState } from 'react'
import { useToast } from "@chakra-ui/react";

const Signup = () => {
    const [show, setShow] = useState(false);
    const [name, setName] = useState();
    const [email, setEmail] = useState();
    const [confirmpassword, setConfirmPassword] = useState();
    const [password, setPassword] = useState();
    const [pic, setPic] = useState();
    const [loading, setLoading] = useState(false)
    const toast = useToast();



    const handleClick = () => setShow(!show);

    const postDetails = (pics) => {
      setLoading(true);
      if (pics === undefined) {
        toast({
          title: "Please Select an Image!",
          status: "warning",
          duration: 5000,
          isClosable: true,
          position: "bottom", 
        });
        return;
      }

      if (pics.type === "image/jpeg" || pics.type === "image/png") {
        const data = new FormData();
        data.append("file", pics);
        data.append("upload_preset", "dosthi-chat");
        data.append("cloud_name", "dosthi-chat");
        fetch("https://api.cloudinary.com/v1_1/dosthi-chat", {
          method: "post",
          body: data,
        }).then((res) => res.json())
        .then((data) => {
          setPic(data.url.toString());
          console.log(data.url.toString());
          setLoading(false);
          })
          .catch((err) => {
            console.log(err);
            setLoading(false);
          });
      } else {
         toast({
           title: "Please Select an Image!",
           status: "warning",
           duration: 5000,
           isClosable: true,
           position: "bottom",
         });
         setLoading(false);
         return;

      }
    };


    const submitHandler = async () => {
      setLoading(true);
      if (!name || !email || !password || !confirmpassword){
        toast({
          title: "Please Fill all the Feilds",
          status: "warning",
          duration: 5000,
          isClosable: true,
          position: "bottom",
        });
        setLoading(false);
        return;
      }
      if (password !== confirmpassword) {
      toast({
          title: "Password Do Not Match",
          status: "warning",
          duration: 5000,
          isClosable: true,
          position: "bottom",
        });
        return;
      }

      try {
        const config = {
          headers: {
          "Content-type": "application/json",
        },
      };
      const { data } = await axios.post()
      } catch (error) {
        
      }
    };
    
  return (
    <VStack spacing="5px" color="black">
      <FormControl id="first-name" isRequired>
        <FormLabel>Name</FormLabel>
        <Input
          placeholder="Enter Your Name"
          onChange={(e) => setName(e.target.value)}
        />
      </FormControl>
      <FormControl id="Email" isRequired>
        <FormLabel>Email</FormLabel>
        <Input
          placeholder="Enter Your Email"
          onChange={(e) => setEmail(e.target.value)}
        />
      </FormControl>
      <FormControl id="Password" isRequired>
        <FormLabel>Password</FormLabel>
        <InputGroup>
          <Input
            type={show? "text" : "password"}
            placeholder="Enter Your Password"
            onChange={(e) => setPassword(e.target.value)}
          />
          <InputRightElement width='4.5rem'>
            <Button h='1.75rem' size='sm' onClick={handleClick}>
                {show ? "Hide" : "Show"}
                </Button>
          </InputRightElement>
        </InputGroup>
      </FormControl>
      <FormControl id="Password" isRequired>
        <FormLabel>Confirm Password</FormLabel>
        <InputGroup size="md">
          <Input
            type={show? "text" : "password"}
            placeholder="Confirm Password"
            onChange={(e) => setConfirmPassword(e.target.value)}
          />
          <InputRightElement width='4.5rem'>
            <Button h='1.75rem' size='sm' onClick={handleClick}>
                {show ? "Hide" : "Show"}
                </Button>
          </InputRightElement>
        </InputGroup>
      </FormControl>
      <FormControl id="Pic" >
        <FormLabel>Upload Your Picture</FormLabel>
        <input 
        type="file"
        onPlay={1.5}
        accept='image/*'
        onChange={(e)=> postDetails(e.target.files[0])}
        />
      </FormControl>
      
      <Button
      colorScheme='blue'
      width='100%'
      style={{marginTop: 15 }}
      onClick={submitHandler}
      isLoading={loading}
      >
        Sign Up
      </Button>
    </VStack>
  );
};

export default Signup
